<?php

namespace BrBundle\Repository;

/**
 * Company
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class Company extends \CommonBundle\Component\Doctrine\ORM\EntityRepository
{
    /**
     * @return \Doctrine\ORM\Query
     */
    public function findAllQuery()
    {
        $query = $this->getEntityManager()->createQueryBuilder();
        return $query->select('c')
            ->from('BrBundle\Entity\Company', 'c')
            ->where(
                $query->expr()->eq('c.active', 'true')
            )
            ->orderBy('c.name', 'ASC')
            ->getQuery();
    }

    /**
     * @return \Doctrine\ORM\Query
     */
    public function findAllByNameQuery($name)
    {
        $query = $this->getEntityManager()->createQueryBuilder();
        return $query->select('c')
            ->from('BrBundle\Entity\Company', 'c')
            ->where(
                $query->expr()->andX(
                    $query->expr()->eq('c.active', 'true'),
                    $query->expr()->like($query->expr()->lower('c.name'), ':name')
                )
            )
            ->setParameter('name', '%' . strtolower($name) . '%')
            ->orderBy('c.name', 'ASC')
            ->getQuery();
    }

    /**
     * @return \Doctrine\ORM\Query
     */
    public function findAllAttendingJobfair()
    {
        $query = $this->getEntityManager()->createQueryBuilder();
        return $query->select('c')
            ->from('BrBundle\Entity\Company', 'c')
            ->where(
                $query->expr()->eq('c.attendsJobfair', 'true'),
            )
            ->orderBy('c.name', 'ASC')
            ->getQuery();
    }
}
