<?php

namespace BrBundle\Repository\Contract;

use BrBundle\Entity\Contract;

/**
 * History
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class History extends \CommonBundle\Component\Doctrine\ORM\EntityRepository
{
    /**
     * @param  Contract $contract
     * @return \Doctrine\ORM\Query
     */
    public function findAllContractVersions(Contract $contract)
    {
        $query = $this->getEntityManager()->createQueryBuilder();
        return $query->select('h')
            ->from('BrBundle\Entity\Contract\History', 'h')
            ->where(
                $query->expr()->eq('h.contract', ':contract')
            )
            ->setParameter('contract', $contract)
            ->orderBy('h.version', 'ASC')
            ->getQuery();
    }
}
