<?php

namespace BrBundle\Repository\Event;

use BrBundle\Entity\Event;
use BrBundle\Entity\Event\CompanyMap;
use BrBundle\Entity\Event\Subscription;

/**
 * Connection
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 * Formerly known as Match, but due to php8 this isn't allowed.
 */
class Connection extends \CommonBundle\Component\Doctrine\ORM\EntityRepository
{
    public function findByMapAndSubscription(CompanyMap $companyMap, Subscription $subscription)
    {
        $query = $this->getEntityManager()->createQueryBuilder();
        return $query->select('m')
            ->from('BrBundle\Entity\Event\Connection', 'm')
            ->where(
                $query->expr()->andX(
                    $query->expr()->eq('m.companyMap', ':companyMap'),
                    $query->expr()->eq('m.subscription', ':subscription')
                )
            )
            ->setParameter('companyMap', $companyMap->getId())
            ->setParameter('subscription', $subscription->getId())
            ->getQuery()
            ->getResult();
    }

    public function findAllByCompanyMapQuery(CompanyMap $companyMap)
    {
        $query = $this->getEntityManager()->createQueryBuilder();
        return $query->select('m')
            ->from('BrBundle\Entity\Event\Connection', 'm')
            ->where(
                $query->expr()->eq('m.companyMap', ':companyMap')
            )
            ->orderBy('m.timestamp')
            ->setParameter('companyMap', $companyMap->getId())
            ->getQuery();
    }

    public function findAllByEvent(Event $event)
    {
        $query = $this->getEntityManager()->createQueryBuilder();
        return $query->select('m')
            ->from('BrBundle\Entity\Event\Connection', 'm')
            ->join('m.companyMap', 'c')
            ->where(
                $query->expr()->eq('c.event', ':event')
            )
            ->setParameter('event', $event->getId())
            ->getQuery()
            ->getResult();
    }
}
