<?php

namespace BrBundle\Repository\Event;

use BrBundle\Entity\Event\CompanyMap;
use BrBundle\Entity\Event\Subscription;

/**
 * Match
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class Match extends \CommonBundle\Component\Doctrine\ORM\EntityRepository
{
    public function findByMapAndSubscription(CompanyMap $companyMap, Subscription $subscription)
    {
        $query = $this->getEntityManager()->createQueryBuilder();
        return $query->select('m')
            ->from('BrBundle\Entity\Event\Match', 'm')
            ->where(
                $query->expr()->andX(
                    $query->expr()->eq('m.companyMap', ':companyMap'),
                    $query->expr()->eq('m.subscription', ':subscription')
                )
            )
            ->setParameter('companyMap', $companyMap->getId())
            ->setParameter('subscription', $subscription->getId())
            ->getQuery()
            ->getResult();
    }


    public function findAllByCompanyMapQuery(CompanyMap $companyMap)
    {
        $query = $this->getEntityManager()->createQueryBuilder();
        return $query->select('m')
            ->from('BrBundle\Entity\Event\Match', 'm')
            ->where(
                $query->expr()->eq('m.companyMap', ':companyMap')
            )
            ->orderBy()
            ->setParameter('companyMap', $companyMap->getId())
            ->getQuery();
    }
    
}
