<?php

namespace BrBundle\Repository\Invoice;

use BrBundle\Entity\Product\Order;

/**
 * Contract
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class Contract extends \Doctrine\ORM\EntityRepository
{
    /**
     * @param  Order $order
     * @return \Doctrine\ORM\Query
     */
    public function findAllByOrderQuery(Order $order)
    {
        $query = $this->getEntityManager()->createQueryBuilder();
        return $query->select('i')
            ->from('BrBundle\Entity\Invoice\Contract', 'i')
            ->where(
                $query->expr()->eq('i.order', ':order')
            )
            ->setParameter('order', $order)
            ->getQuery();
    }
}
