<?php

namespace BrBundle\Repository\Match\Profile;

use BrBundle\Entity\Match\Feature;
use BrBundle\Entity\Match\Profile;

/**
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class ProfileFeatureMap extends \CommonBundle\Component\Doctrine\ORM\EntityRepository
{
    public function findOneByProfileAndFeature(Profile $profile, Feature $feature)
    {
        $query = $this->getEntityManager()->createQueryBuilder();
        return $query->select('p')
            ->from('BrBundle\Entity\Match\Profile\ProfileFeatureMap', 'p')
            ->where(
                $query->expr()->andX(
                    $query->expr()->eq('p.profile', ':profile'),
                    $query->expr()->eq('p.feature', ':feature')
                )
            )
            ->setParameter('profile', $profile)
            ->setParameter('feature', $feature)
            ->getQuery()
            ->getOneOrNullResult();
    }

    public function findOneByProfileAndFeatureId(Profile $profile, string $featureId)
    {
        $query = $this->getEntityManager()->createQueryBuilder();
        return $query->select('p')
            ->from('BrBundle\Entity\Match\Profile\ProfileFeatureMap', 'p')
            ->innerJoin('p.feature', 'f')
            ->where(
                $query->expr()->andX(
                    $query->expr()->eq('p.profile', ':profile'),
                    $query->expr()->eq('f.id', ':feature')
                )
            )
            ->setParameter('profile', $profile)
            ->setParameter('feature', $featureId)
            ->getQuery()
            ->getOneOrNullResult();
    }
}
