<?php

namespace CommonBundle\Repository\General;

use RuntimeException;

/**
 * Config
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class Config extends \CommonBundle\Component\Doctrine\ORM\EntityRepository
{
    /**
     * @return \Doctrine\ORM\Query
     */
    public function findAllQuery()
    {
        $query = $this->getEntityManager()->createQueryBuilder();
        return $query->select('c')
            ->from('CommonBundle\Entity\General\Config', 'c')
            ->orderBy('c.key', 'ASC')
            ->getQuery();
    }

    /**
     * @param  string $prefix
     * @return array
     */
    public function findAllByPrefix($prefix)
    {
        $query = $this->getEntityManager()->createQueryBuilder();
        $configs = $query->select('c')
            ->from('CommonBundle\Entity\General\Config', 'c')
            ->where(
                $query->expr()->like('c.key', ':prefix')
            )
            ->setParameter('prefix', $prefix . '.%')
            ->getQuery()
            ->getResult();

        $result = array();
        foreach ($configs as $config) {
            $key = $config->getKey();
            $value = $config->getValue();

            $key = str_replace($prefix . '.', '', $key);

            $result[$key] = $value;
        }

        return $result;
    }

    /**
     * @param  string $key
     * @return string
     */
    public function getConfigValue($key)
    {
        $config = $this->find($key);

        if ($config === null) {
            throw new RuntimeException('Configuration entry ' . $key . ' not found');
        }

        return $config->getValue();
    }
}
