<?php

namespace CommonBundle\Repository\General;

/**
 * Address
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class Location extends \CommonBundle\Component\Doctrine\ORM\EntityRepository
{
    /**
     * @return \Doctrine\ORM\Query
     */
    public function findAllActiveQuery()
    {
        $query = $this->getEntityManager()->createQueryBuilder();
        return $query->select('l')
            ->from('CommonBundle\Entity\General\Location', 'l')
            ->where(
                $query->expr()->eq('l.active', 'true')
            )
            ->orderBy('l.name', 'ASC')
            ->getQuery();
    }
}
