<?php

namespace CommonBundle\Repository\General;

use RuntimeException;

/**
 * Organization
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class Organization extends \CommonBundle\Component\Doctrine\ORM\EntityRepository
{
    /**
     * @return \Doctrine\ORM\Query
     */
    public function findAllQuery()
    {
        $query = $this->getEntityManager()->createQueryBuilder();
        return $query->select('o')
            ->from('CommonBundle\Entity\General\Organization', 'o')
            ->orderBy('o.name', 'ASC')
            ->getQuery();
    }

    /**
     * @return \CommonBundle\Entity\General\Organization
     */
    public function findOne()
    {
        if (count($this->findAll()) > 1) {
            throw new RuntimeException('There is more than one organization');
        }

        return $this->findAll()[0];
    }
}
