<?php

namespace CommonBundle\Repository\General\Organization;

/**
 * Unit
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class Unit extends \CommonBundle\Component\Doctrine\ORM\EntityRepository
{
    /**
     * @return \Doctrine\ORM\Query
     */
    public function findAllActiveQuery()
    {
        $query = $this->getEntityManager()->createQueryBuilder();
        return $query->select('u')
            ->from('CommonBundle\Entity\General\Organization\Unit', 'u')
            ->where(
                $query->expr()->eq('u.active', 'true')
            )
            ->orderBy('u.name', 'ASC')
            ->getQuery();
    }

    /**
     * @return \Doctrine\ORM\Query
     */
    public function findAllActiveAndDisplayedQuery()
    {
        $query = $this->getEntityManager()->createQueryBuilder();
        return $query->select('u')
            ->from('CommonBundle\Entity\General\Organization\Unit', 'u')
            ->where(
                $query->expr()->andX(
                    $query->expr()->eq('u.active', 'true'),
                    $query->expr()->eq('u.displayed', 'true')
                )
            )
            ->orderBy('u.name', 'ASC')
            ->getQuery();
    }

    /**
     * @return \Doctrine\ORM\Query
     */
    public function findAllActiveAndNotDisplayedQuery()
    {
        $query = $this->getEntityManager()->createQueryBuilder();
        return $query->select('u')
            ->from('CommonBundle\Entity\General\Organization\Unit', 'u')
            ->where(
                $query->expr()->andX(
                    $query->expr()->eq('u.active', 'true'),
                    $query->expr()->eq('u.displayed', 'false')
                )
            )
            ->orderBy('u.name', 'ASC')
            ->getQuery();
    }

    /**
     * @return \Doctrine\ORM\Query
     */
    public function findOneByName($name)
    {
        $query = $this->getEntityManager()->createQueryBuilder();
        return $query->select('u')
            ->from('CommonBundle\Entity\General\Unit', 'u')
            ->where(
                $query->expr()->eq('u.name', ':name')
            )
            ->setParameter('name', $name)
            ->setMaxResults(1)
            ->getQuery()
            ->getOneOrNullResult();
    }

    /**
     * @return \Doctrine\ORM\Query
     */
    public function findAllQuery()
    {
        $query = $this->getEntityManager()->createQueryBuilder();
        return $query->select('u')
            ->from('CommonBundle\Entity\General\Organization\Unit', 'u')
            ->orderBy('u.name', 'ASC')
            ->getQuery();
    }
}
