<?php

namespace CommonBundle\Repository\User\Person\Organization;

use CommonBundle\Entity\General\AcademicYear;
use CommonBundle\Entity\General\Organization;
use CommonBundle\Entity\User\Person\Academic;

/**
 * AcademicYearMap
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class AcademicYearMap extends \CommonBundle\Component\Doctrine\ORM\EntityRepository
{
    /**
     * @param  Academic     $academic
     * @param  AcademicYear $academicYear
     * @return \CommonBundle\Entity\User\Person\Organization\AcademicYearMap|null
     */
    public function findOneByAcademicAndAcademicYear(Academic $academic, AcademicYear $academicYear)
    {
        $query = $this->getEntityManager()->createQueryBuilder();
        return $query->select('m')
            ->from('CommonBundle\Entity\User\Person\Organization\AcademicYearMap', 'm')
            ->where(
                $query->expr()->andX(
                    $query->expr()->eq('m.academic', ':academic'),
                    $query->expr()->eq('m.academicYear', ':academicYear')
                )
            )
            ->setParameter('academic', $academic->getId())
            ->setParameter('academicYear', $academicYear)
            ->setMaxResults(1)
            ->getQuery()
            ->getOneOrNullResult();
    }

    /**
     * @param  AcademicYear      $academicYear
     * @param  Organization|null $organization
     * @return \Doctrine\ORM\Query
     */
    public function findAllByAcademicYearAndOrganizationQuery(AcademicYear $academicYear, Organization $organization = null)
    {
        $query = $this->getEntityManager()->createQueryBuilder();
        $query->select('m')
            ->from('CommonBundle\Entity\User\Person\Organization\AcademicYearMap', 'm')
            ->where(
                $query->expr()->andX(
                    $organization !== null ? $query->expr()->eq('m.organization', ':organization') : '1=1',
                    $query->expr()->eq('m.academicYear', ':academicYear')
                )
            )
            ->setParameter('academicYear', $academicYear);

        if ($organization !== null) {
            $query->setParameter('organization', $organization);
        }

        return $query->getQuery();
    }
}
