<?php

namespace CommonBundle\Repository\User\Person\Organization;

use CommonBundle\Entity\General\AcademicYear;
use CommonBundle\Entity\General\Organization\Unit;

/**
 * UnitMap
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class UnitMap extends \CommonBundle\Component\Doctrine\ORM\EntityRepository
{
    /**
     * @param  Unit         $unit
     * @param  AcademicYear $academicYear
     * @return \Doctrine\ORM\Query
     */
    public function findAllByUnitAndAcademicYearQuery(Unit $unit, AcademicYear $academicYear)
    {
        $query = $this->getEntityManager()->createQueryBuilder();
        return $query->select('u')
            ->from('CommonBundle\Entity\User\Person\Organization\UnitMap', 'u')
            ->where(
                $query->expr()->andX(
                    $query->expr()->eq('u.unit', ':unit'),
                    $query->expr()->eq('u.academicYear', ':academicYear')
                )
            )
            ->setParameter('unit', $unit)
            ->setParameter('academicYear', $academicYear)
            ->getQuery();
    }

    /**
     * @param  AcademicYear $academicYear
     * @return \Doctrine\ORM\Query
     */
    public function findAllWorkgroupMembersByAcademicYearQuery(AcademicYear $academicYear)
    {
        $query = $this->getEntityManager()->createQueryBuilder();
        return $query->select('m')
            ->from('CommonBundle\Entity\User\Person\Organization\UnitMap', 'm')
            ->innerJoin('m.unit', 'u')
            ->where(
                $query->expr()->andX(
                    $query->expr()->eq('m.academicYear', ':academicYear'),
                    $query->expr()->eq('u.workgroup', 'true'),
                )
            )
            ->setParameter('academicYear', $academicYear)
            ->orderBy('u.name', 'ASC')
            ->getQuery();
    }
}
