<?php

namespace CudiBundle\Repository\Comment;

use CudiBundle\Entity\Article;
use CudiBundle\Entity\Comment\Comment as CommentEntity;

/**
 * ArticleMap
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class ArticleMap extends \CommonBundle\Component\Doctrine\ORM\EntityRepository
{
    /**
     * @param  Article       $article
     * @param  CommentEntity $comment
     * @return \CudiBundle\Entity\Comment\ArticleMap|null
     */
    public function findOneByArticleAndComment(Article $article, CommentEntity $comment)
    {
        $query = $this->getEntityManager()->createQueryBuilder();
        return $query->select('m')
            ->from('CudiBundle\Entity\Comment\ArticleMap', 'm')
            ->where(
                $query->expr()->andX(
                    $query->expr()->eq('m.article', ':article'),
                    $query->expr()->eq('m.comment', ':comment')
                )
            )
            ->setParameter('article', $article)
            ->setParameter('comment', $comment)
            ->setMaxResults(1)
            ->getQuery()
            ->getOneOrNullResult();
    }

    /**
     * @param  Article $article
     * @return \Doctrine\ORM\Query
     */
    public function findAllByArticleQuery(Article $article)
    {
        $query = $this->getEntityManager()->createQueryBuilder();
        return $query->select('m')
            ->from('CudiBundle\Entity\Comment\ArticleMap', 'm')
            ->where(
                $query->expr()->eq('m.article', ':article')
            )
            ->setParameter('article', $article)
            ->getQuery();
    }
}
