    /**
     * @param  ArticleEntity $article
     * @param  Person        $person
     * @param  boolean       $limitByPeriod
     * @return \CudiBundle\Entity\Sale\Booking|null
     */
    public function findOneSoldByArticleAndPerson(ArticleEntity $article, Person $person, $limitByPeriod = true)
    {
        $period = $this->getEntityManager()
            ->getRepository('CudiBundle\Entity\Stock\Period')
            ->findOneActive();

        $query = $this->getEntityManager()->createQueryBuilder();
        $query->select('b')
            ->from('CudiBundle\Entity\Sale\Booking', 'b')
            ->where(
                $query->expr()->andX(
                    $query->expr()->eq('b.person', ':person'),
                    $query->expr()->eq('b.article', ':article'),
                    $query->expr()->eq('b.status', '\'sold\''),
                    !$limitByPeriod ? '1=1' : $query->expr()->gte('b.bookDate', ':startDate'),
                    $period->isOpen() && !$limitByPeriod ? '1=1' : $query->expr()->lt('b.bookDate', ':endDate')
                )
            )
            ->addOrderBy('b.saleDate', 'DESC')
            ->setParameter(':person', $person->getId())
            ->setParameter(':article', $article->getId());

        if ($limitByPeriod) {
            $query->setParameter('startDate', $period->getStartDate());
        }

        if (!$period->isOpen() && $limitByPeriod) {
            $query->setParameter('endDate', $period->getEndDate());
        }

        return $query->setMaxResults(1)
            ->getQuery()
            ->getOneOrNullResult();
    }

    /**
     * @param  ArticleEntity $article
     * @param  Person        $person
     * @return \CudiBundle\Entity\Sale\Booking|null
     */
    public function findOneSoldOrAssignedOrBookedByArticleAndPerson(ArticleEntity $article, Person $person)
    {
        $period = $this->getEntityManager()
            ->getRepository('CudiBundle\Entity\Stock\Period')
            ->findOneActive();

        return $this->findOneSoldOrAssignedOrBookedByArticleAndPersonBetween($article, $person, $period->getStartDate(), $period->getEndDate());
    }

