<?php

namespace CudiBundle\Repository\Sale;

/**
 * Person
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class BoughtItems extends \CommonBundle\Component\Doctrine\ORM\EntityRepository
{
    /**
     * @param int $personId
     * @return \Doctrine\ORM\Query
     */
    public function findAllByPersonId($personId)
    {
        $query = $this->getEntityManager()->createQueryBuilder();
        return $query->select('bi')
            ->from('CommonBundle\Entity\Sale\Booking', 'bi')
            ->where('bi.person = :personId')
            ->setParameter('personId', $personId)
            ->getQuery()
            ->getResult();
    }
}
