<?php

namespace CudiBundle\Repository\Articles;

use CudiBundle\Entity\Article,
    Doctrine\ORM\EntityRepository;

/**
 * Comment
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class Comment extends EntityRepository
{
    public function findAllByArticle(Article $article)
    {
        $query = $this->_em->createQueryBuilder();
        $resultSet = $query->select('c')
        	->from('CudiBundle\Entity\Articles\Comment', 'c')
        	->where($query->expr()->eq('c.article', ':article'))
        	->setParameter('article', $article->getId())
        	->orderBy('c.date', 'DESC')
        	->getQuery()
        	->getResult();
        	
        return $resultSet;
    }
}