<?php

namespace CudiBundle\Repository\Articles\Discount;

use CudiBundle\Entity\Article,
    CudiBundle\Entity\Articles\Discount\Type as TypeEntity,
    Doctrine\ORM\EntityRepository;

/**
 * Discount
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class Discount extends EntityRepository
{
    public function findAllByArticle(Article $article)
    {
        $query = $this->_em->createQueryBuilder();
        $resultSet = $query->select('d')
        	->from('CudiBundle\Entity\Articles\Discount\Discount', 'd')
        	->where(
        	    $query->expr()->eq('d.article', ':article')
        	)
        	->setParameter('article', $article->getId())
        	->getQuery()
        	->getResult();
        	
        return $resultSet;
    }
    
    public function findOneByArticleAndType(Article $article, TypeEntity $type)
    {
        $query = $this->_em->createQueryBuilder();
        $resultSet = $query->select('d')
        	->from('CudiBundle\Entity\Articles\Discount\Discount', 'd')
        	->where(
        	    $query->expr()->andX(
        	        $query->expr()->eq('d.article', ':article'),
        	        $query->expr()->eq('d.type', ':type')
        	    )
        	)
        	->setParameter('article', $article->getId())
        	->setParameter('type', $type->getId())
        	->setMaxResults(1)
        	->getQuery()
        	->getResult();
        	
        if (isset($resultSet[0]))
        	return $resultSet[0];
        
        return null;
    }
}