<?php

namespace CudiBundle\Repository\Sales\Session;

use CudiBundle\Entity\Sales\Session,
    Doctrine\ORM\EntityRepository;

/**
 * Restriction
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class Restriction extends EntityRepository
{
    public function findOneBySessionAndType(Session $session, $type)
    {
        $query = $this->_em->createQueryBuilder();
        $resultSet = $query->select('r')
            ->from('CudiBundle\Entity\Sales\Session\Restriction', 'r')
            ->where(
                $query->expr()->andX(
                    $query->expr()->eq('r.session', ':session'),
                    $query->expr()->eq('r.type', ':type')
                )
            )
            ->setParameter('type', $type)
            ->setParameter('session', $session)
            ->setMaxResults(1)
            ->getQuery()
            ->getResult();

       if (isset($resultSet[0]))
           return $resultSet[0];

       return null;
    }
}
