<?php

namespace FormBundle\Repository;

use FormBundle\Entity\Field as FieldEntity;
use FormBundle\Entity\Node\Entry as FormEntryEntity;

/**
 * Entry
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class Entry extends \CommonBundle\Component\Doctrine\ORM\EntityRepository
{
    public function findAllByFieldQuery(FieldEntity $field)
    {
        $query = $this->getEntityManager()->createQueryBuilder();
        return $query->select('n')
            ->from('FormBundle\Entity\Entry', 'n')
            ->where(
                $query->expr()->eq('n.field', ':field')
            )
            ->setParameter('field', $field)
            ->getQuery();
    }

    public function findAllByFormEntryQuery(FormEntryEntity $formEntry)
    {
        $query = $this->getEntityManager()->createQueryBuilder();
        return $query->select('n')
            ->from('FormBundle\Entity\Entry', 'n')
            ->innerJoin('n.field', 'f')
            ->where(
                $query->expr()->eq('n.formEntry', ':formEntry')
            )
            ->setParameter('formEntry', $formEntry)
            ->orderBy('f.order', 'ASC')
            ->getQuery();
    }

    public function findOneByFormEntryAndField(FormEntryEntity $formEntry, FieldEntity $field)
    {
        $query = $this->getEntityManager()->createQueryBuilder();
        return $query->select('n')
            ->from('FormBundle\Entity\Entry', 'n')
            ->where(
                $query->expr()->andx(
                    $query->expr()->eq('n.field', ':field'),
                    $query->expr()->eq('n.formEntry', ':form')
                )
            )
            ->setParameter('field', $field)
            ->setParameter('form', $formEntry)
            ->setMaxResults(1)
            ->getQuery()
            ->getOneOrNullResult();
    }
}
