<?php

namespace FormBundle\Repository;

use FormBundle\Entity\Node\Form;

/**
 * Field
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class Field extends \CommonBundle\Component\Doctrine\ORM\EntityRepository
{
    public function findAllByFormQuery(Form $form)
    {
        $query = $this->getEntityManager()->createQueryBuilder();
        return $query->select('n')
            ->from('FormBundle\Entity\Field', 'n')
            ->where(
                $query->expr()->eq('n.form', ':form')
            )
            ->setParameter('form', $form)
            ->orderBy('n.order', 'ASC')
            ->getQuery();
    }

    public function findLatestField(Form $form)
    {
        $query = $this->getEntityManager()->createQueryBuilder();
        return $query->select('n')
            ->from('FormBundle\Entity\Field', 'n')
            ->where(
                $query->expr()->eq('n.form', ':form')
            )
            ->setParameter('form', $form)
            ->orderBy('n.id', 'DESC')
            ->setMaxResults(1)
            ->getQuery()
            ->getOneOrNullResult();
    }
}
