<?php

namespace FormBundle\Repository\Node;

use DateTime;

/**
 * Group
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class Group extends \CommonBundle\Component\Doctrine\ORM\EntityRepository
{
    public function findAllQuery()
    {
        $query = $this->getEntityManager()->createQueryBuilder();
        return $query->select('n')
            ->from('FormBundle\Entity\Node\Group', 'n')
            ->orderBy('n.creationTime', 'DESC')
            ->getQuery();
    }

    public function findAllActive()
    {
        $query = $this->getEntityManager()->createQueryBuilder();
        return $query->select('g')
            ->from('FormBundle\Entity\Node\Form\GroupMap', 'm')
            ->from('FormBundle\Entity\Node\Group', 'g')
            ->innerJoin('m.form', 'f')
            ->where(
                $query->expr()->andX(
                    $query->expr()->gt('f.endDate', ':now'),
                    $query->expr()->eq('m.group', 'g')
                )
            )
            ->setParameter('now', new DateTime())
            ->getQuery()
            ->getResult();
    }

    public function findAllOld()
    {
        $query = $this->getEntityManager()->createQueryBuilder();
        return $query->select('g')
            ->from('FormBundle\Entity\Node\Form\GroupMap', 'm')
            ->from('FormBundle\Entity\Node\Group', 'g')
            ->innerJoin('m.form', 'f')
            ->where(
                $query->expr()->andX(
                    $query->expr()->lt('f.endDate', ':now'),
                    $query->expr()->eq('m.group', 'g')
                )
            )
            ->setParameter('now', new DateTime())
            ->getQuery()
            ->getResult();
    }
}
