<?php

namespace GalleryBundle\Repository;

use DateTime;

/**
 * Album
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class Album extends \CommonBundle\Component\Doctrine\ORM\EntityRepository
{
    public function findAllQuery()
    {
        $query = $this->getEntityManager()->createQueryBuilder();
        return $query->select('a')
            ->from('GalleryBundle\Entity\Album', 'a')
            ->orderBy('a.dateActivity', 'DESC')
            ->getQuery();
    }

    public function findAllFromToQuery(DateTime $start, DateTime $end)
    {
        $query = $this->getEntityManager()->createQueryBuilder();
        return $query->select('a')
            ->from('GalleryBundle\Entity\Album', 'a')
            ->where(
                $query->expr()->andX(
                    $query->expr()->gte('a.dateActivity', ':start'),
                    $query->expr()->lt('a.dateActivity', ':end')
                )
            )
            ->orderBy('a.dateActivity', 'ASC')
            ->setParameter('start', $start)
            ->setParameter('end', $end)
            ->getQuery();
    }
}
