<?php

namespace LogisticsBundle\Repository;

use CommonBundle\Component\Util\EntityRepository,
    CommonBundle\Entity\General\AcademicYear;

/**
 * Driver
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class Driver extends EntityRepository
{
    public function findOneById($id)
    {
        $query = $this->_em->createQueryBuilder();
        $resultSet = $query->select('d')
            ->from('LogisticsBundle\Entity\Driver', 'd')
            ->where(
                $query->expr()->andX(
                    $query->expr()->eq('d.removed', 'false'),
                    $query->expr()->eq('d.person', ':id')
                )
            )
            ->setParameter('id', $id)
            ->getQuery()
            ->getResult();

        if (isset($resultSet[0]))
            return $resultSet[0];
        return null;
    }

    public function findAllByYear(AcademicYear $year) {

        $query = $this->_em->createQueryBuilder();
        $resultSet = $query->select('d')
            ->from('LogisticsBundle\Entity\Driver', 'd')
            ->innerJoin('d.years', 'y')
            ->where(
                $query->expr()->andX(
                    $query->expr()->eq('d.removed', 'false'),
                    $query->expr()->eq('y', ':year')
                )
            )
            ->setParameter('year', $year)
            ->getQuery()
            ->getResult();

        return $resultSet;
    }

    public function findAll()
    {
        $query = $this->_em->createQueryBuilder();
        $resultSet = $query->select('d')
            ->from('LogisticsBundle\Entity\Driver', 'd')
            ->innerJoin('d.person', 'p')
            ->where(
                $query->expr()->eq('d.removed', 'false')
            )
            ->orderBy('p.lastName', 'ASC')
            ->getQuery()
            ->getResult();
        return $resultSet;
    }
}
