<?php

namespace LogisticsBundle\Repository\Lease;

use CommonBundle\Component\Doctrine\ORM\EntityRepository,
    LogisticsBundle\Entity\Lease\Item;

/**
 * Lease
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class Lease extends EntityRepository
{
    /**
     * Finds all leases of an item that have not yet been returned
     *
     * @param \LogisticsBundle\Entity\Lease\Item $item
     * @return array
     */
    public function findUnreturnedByItemQuery(Item $item)
    {
        $query = $this->_em->createQueryBuilder();
        $resultSet = $query->select('l')
            ->from('LogisticsBundle\Entity\Lease\Lease', 'l')
            ->where(
                $query->expr()->andX(
                    $query->expr()->eq('l.returned', 'false'),
                    $query->expr()->eq('l.item', ':item')
                )
            )
            ->setParameter('item')
            ->getQuery();

        return $resultSet;
    }

    /**
     * Finds all leases that have not yet been returned
     *
     * @return array
     */
    public function findAllUnreturnedQuery()
    {
        $query = $this->_em->createQueryBuilder();
        $resultSet = $query->select('l')
            ->from('LogisticsBundle\Entity\Lease\Lease', 'l')
            ->where(
                $query->expr()->eq('l.returned', 'false'),
            )
            ->setParameter('item')
            ->getQuery();

        return $resultSet;
    }
}
