<?php

/**
 * Preference
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */

namespace MailBundle\Repository;

class Preference extends \CommonBundle\Component\Doctrine\ORM\EntityRepository
{
    public function findAllQuery()
    {
        $query = $this->getEntityManager()->createQueryBuilder();
        return $query->select('a')
            ->from('MailBundle\Entity\Preference', 'a')
            ->getQuery();
    }

    public function findAllByNameQuery($name)
    {
        $query = $this->getEntityManager()->createQueryBuilder();
        return $query->select('a')
            ->from('MailBundle\Entity\Preference', 'a')
            ->where(
                $query->expr()->like('a.name', ':name')
            )
            ->setParameter('name', '%' . strtolower($name) . '%')
            ->getQuery();
    }
}
