<?php

namespace OnBundle\Repository;

/**
 * Slug
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class Slug extends \CommonBundle\Component\Doctrine\ORM\EntityRepository
{
    /**
     * @param  string $name
     * @return \Doctrine\ORM\Query
     */
    public function findAllByNameQuery($name)
    {
        $query = $this->getEntityManager()->createQueryBuilder();
        return $query->select('s')
            ->from('OnBundle\Entity\Slug', 's')
            ->where(
                $query->expr()->like($query->expr()->lower('s.name'), ':name')
            )
            ->setParameter('name', '%' . strtolower($name) . '%')
            ->orderBy('s.name')
            ->getQuery()->getResult();
    }

    /**
     * @return \Doctrine\ORM\Query
     */
    public function findAllActive()
    {
        $query = $this->getEntityManager()->createQueryBuilder();
        return $query->select('s')
            ->from('OnBundle\Entity\Slug', 's')
            ->where(
                $query->expr()->eq('s.active', 'true')
            )
            ->orderBy('s.name')
            ->getQuery()->getResult();
    }

    /**
     * @return \Doctrine\ORM\Query
     */
    public function findAllOld()
    {
        $query = $this->getEntityManager()->createQueryBuilder();
        return $query->select('s')
            ->from('OnBundle\Entity\Slug', 's')
            ->where(
                $query->expr()->eq('s.active', 'false')
            )
            ->orderBy('s.name')
            ->getQuery()->getResult();
    }
}
