<?php

namespace PageBundle\Repository;

/**
 * Category
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class Category extends \CommonBundle\Component\Doctrine\ORM\EntityRepository
{
    public function findAllQuery()
    {
        $query = $this->getEntityManager()->createQueryBuilder();
        return $query->select('c')
            ->from('PageBundle\Entity\Category', 'c')
            ->where(
                $query->expr()->eq('c.active', 'true')
            )
            ->orderBy('c.orderNumber', 'ASC')
            ->getQuery();
    }

    public function findByParent($parent)
    {
        return $this->getEntityManager()
            ->getRepository('PageBundle\Entity\Category')
            ->findBy(array('parent' => $parent, 'active' => true), array('orderNumber' => 'ASC'));
    }
}
