<?php

namespace ProfBundle\Repository\Action\File;

use CudiBundle\Entity\Article,
    CudiBundle\Entity\File,
    Doctrine\ORM\EntityRepository,
    Doctrine\ORM\Query\Expr\Join;

/**
 * Remove
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class Remove extends EntityRepository
{
    public function findOneByFile(File $file)
    {
        $query = $this->_em->createQueryBuilder();
        $resultSet = $query->select('a')
        	->from('ProfBundle\Entity\Action\File\Remove', 'a')
        	->where(
        	    $query->expr()->eq('a.file', ':file')
            )
        	->setParameter('file', $file->getId())
        	->setMaxResults(1)
        	->getQuery()
        	->getResult();
        
        if (isset($resultSet[0]))
        	return $resultSet[0];
        
        return null;
    }
    
    public function findAllByArticle(Article $article)
    {
        $query = $this->_em->createQueryBuilder();
        $resultSet = $query->select('r')
        	->from('ProfBundle\Entity\Action\File\Remove', 'r')
        	->innerJoin('r.file', 'f', Join::WITH, $query->expr()->eq('f.internalArticle', ':article'))
        	->setParameter('article', $article->getId())
        	->getQuery()
        	->getResult();
        
        return $resultSet;
    }
}