<?php

namespace PublicationBundle\Repository;

/**
 * Video
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class Video extends \CommonBundle\Component\Doctrine\ORM\EntityRepository
{
    public function findAllQuery()
    {
        $query = $this->getEntityManager()->createQueryBuilder();
        return $query->select('r')
            ->from('PublicationBundle\Entity\Video', 'r')
            ->orderBy('r.id', 'DESC')
            ->getQuery();
    }

    public function findAllByDate()
    {
        $query = $this->getEntityManager()->createQueryBuilder();
        return $query->select('r')
            ->from('PublicationBundle\Entity\Video', 'r')
            ->orderBy('r.date', 'DESC')
            ->getQuery();
    }

    public function findAllOnHomePageQuery()
    {
        $query = $this->getEntityManager()->createQueryBuilder();
        return $query->select('r')
            ->from('PublicationBundle\Entity\Video', 'r')
            ->where(
                    $query->expr()->eq('r.showOnHomePage', 'true')
            )
            ->orderBy('r.date', 'DESC')
            ->getQuery();
    }
}