<?php

namespace SecretaryBundle\Repository\Syllabus\Enrollment;

use CommonBundle\Entity\General\AcademicYear;
use CommonBundle\Entity\User\Person\Academic;
use SyllabusBundle\Entity\Study as StudyEntity;

/**
 * Study
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class Study extends \CommonBundle\Component\Doctrine\ORM\EntityRepository
{
    public function findAllByStudyQuery(StudyEntity $study)
    {
        $query = $this->getEntityManager()->createQueryBuilder();
        return $query->select('s')
            ->from('SecretaryBundle\Entity\Syllabus\Enrollment\Study', 's')
            ->where(
                $query->expr()->eq('s.study', ':study')
            )
            ->setParameter('study', $study)
            ->getQuery();
    }

    public function findAllByAcademicAndAcademicYearQuery(Academic $academic, AcademicYear $academicYear)
    {
        $query = $this->getEntityManager()->createQueryBuilder();
        return $query->select('s')
            ->from('SecretaryBundle\Entity\Syllabus\Enrollment\Study', 's')
            ->where(
                $query->expr()->andX(
                    $query->expr()->eq('s.academic', ':academic'),
                    $query->expr()->eq('s.academicYear', ':academicYear')
                )
            )
            ->setParameter('academic', $academic)
            ->setParameter('academicYear', $academicYear)
            ->getQuery();
    }

    public function findOneByAcademicAndStudy(Academic $academic, StudyEntity $study)
    {
        $query = $this->getEntityManager()->createQueryBuilder();
        return $query->select('s')
            ->from('SecretaryBundle\Entity\Syllabus\Enrollment\Study', 's')
            ->where(
                $query->expr()->andX(
                    $query->expr()->eq('s.academic', ':academic'),
                    $query->expr()->eq('s.study', ':study')
                )
            )
            ->setParameter('academic', $academic)
            ->setParameter('study', $study)
            ->setMaxResults(1)
            ->getQuery()
            ->getOneOrNullResult();
    }
}
