<?php

namespace ShopBundle\Repository\Reservation;

use CommonBundle\Entity\User\Person;
use DateTime;

/**
 * Ban
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class Ban extends \CommonBundle\Component\Doctrine\ORM\EntityRepository
{
    /**
     * @return \Doctrine\ORM\Query
     */
    public function findActiveQuery()
    {
        $query = $this->getEntityManager()->createQueryBuilder();
        return $query->select('e')
            ->from('ShopBundle\Entity\Reservation\Ban', 'e')
            ->where(
                $query->expr()->orX(
                    $query->expr()->isNull('a.endTimestamp'),
                    $query->expr()->gte('a.endTimestamp', ':now')
                )
            )
            ->setParameter('now', new DateTime())
            ->getQuery();
    }

    /**
     * @return \Doctrine\ORM\Query
     */
    public function findOldQuery()
    {
        $query = $this->getEntityManager()->createQueryBuilder();
        return $query->select('e')
            ->from('ShopBundle\Entity\Reservation\Ban', 'e')
            ->where(
                $query->expr()->andX(
                    $query->expr()->isNotNull('a.endTimestamp'),
                    $query->expr()->lt('a.endTimestamp', ':now')
                )
            )
            ->setParameter('now', new DateTime())
            ->getQuery();
    }

    /**
     * @param Person $person
     * @return \Doctrine\ORM\Query
     */
    public function findAllByPersonQuery(Person $person)
    {
        $queryBuilder = $this->getEntityManager()->createQueryBuilder();
        $query = $queryBuilder->select('count(s.id)')
            ->from('ShopBundle\Entity\Reservation\Ban', 'b');

        $where = $query->expr()->eq('r.person', ':person');

        $query->where(
            $query->expr()->andX(
                $query->expr()->lt('s.startDate', ':now'),
                $where
            )
        );

        return $query->getQuery();
    }
}