<?php

namespace ShopBundle\Repository\Reservation;

/**
 * Permission
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class Permission extends \CommonBundle\Component\Doctrine\ORM\EntityRepository
{
    /**
     * @param $name
     * @return \Doctrine\ORM\Query
     */
    public function findByNameQuery($name)
    {
        $query = $this->getEntityManager()->createQueryBuilder();

        return $query->select('rp')
            ->from('ShopBundle\Entity\Reservation\Permission', 'rp')
            ->join('rp.person', 'p')
            ->where(
                $query->expr()->orX(
                    $query->expr()->like($query->expr()->lower($query->expr()->concat('p.firstName', $query->expr()->concat("' '", 'p.lastName'))), ':name'),
                    $query->expr()->like($query->expr()->lower($query->expr()->concat('p.lastName', $query->expr()->concat("' '", 'p.firstName'))), ':name')
                )
            )
            ->orderBy('p.id', 'ASC')
            ->setParameter('name', '%' . strtolower($name) . '%')
            ->getQuery();
    }

    /**
     * @return \Doctrine\ORM\Query
     */
    public function findAllQuery()
    {
        $query = $this->getEntityManager()->createQueryBuilder();

        return $query->select('rp')
            ->from('ShopBundle\Entity\Reservation\Permission', 'rp')
            ->getQuery();
    }
}
