<?php

namespace SyllabusBundle\Repository\Study;

/**
 * ModuleGroup
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class ModuleGroup extends \CommonBundle\Component\Doctrine\ORM\EntityRepository
{
    /**
     * @return \Doctrine\ORM\Query
     */
    public function findAllQuery()
    {
        $query = $this->getEntityManager()->createQueryBuilder();
        return $query->select('m')
            ->from('SyllabusBundle\Entity\Study\ModuleGroup', 'm')
            ->orderBy('m.title')
            ->getQuery();
    }

    /**
     * @param  string $title
     * @return \Doctrine\ORM\Query
     */
    public function findAllByTitleQuery($title)
    {
        $query = $this->getEntityManager()->createQueryBuilder();
        return $query->select('m')
            ->from('SyllabusBundle\Entity\Study\ModuleGroup', 'm')
            ->where(
                $query->expr()->like($query->expr()->lower('m.title'), ':title')
            )
            ->setParameter('title', '%' . strtolower($title) . '%')
            ->orderBy('m.title')
            ->getQuery();
    }
}
