<?php

namespace TicketBundle\Repository;

use DateTime;

/**
 * Event
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class Event extends \CommonBundle\Component\Doctrine\ORM\EntityRepository
{
    public function findAllQuery()
    {
        $query = $this->getEntityManager()->createQueryBuilder();
        return $query->select('e')
            ->from('TicketBundle\Entity\Event', 'e')
            ->innerJoin('e.activity', 'a')
            ->where(
                $query->expr()->gt('a.startDate', ':now')
            )
            ->setParameter('now', new DateTime())
            ->getQuery();
    }

    public function findOneByEvent(\CalendarBundle\Entity\Node\Event $event)
    {
        $query = $this->getEntityManager()->createQueryBuilder();
        return $query->select('e')
            ->from('TicketBundle\Entity\Event', 'e')
            ->where(
                $query->expr()->eq('e.activity', ':event')
            )
            ->setParameter('event', $event)
            ->getQuery()
            ->getOneOrNullResult();
    }

    public function findOldQuery()
    {
        $query = $this->getEntityManager()->createQueryBuilder();
        return $query->select('e')
            ->from('TicketBundle\Entity\Event', 'e')
            ->innerJoin('e.activity', 'a')
            ->where(
                $query->expr()->lt('a.startDate', ':now')
            )
            ->setParameter('now', new DateTime())
            ->orderBy('e.bookingsCloseDate', 'DESC')
            ->getQuery();
    }
}
